from doordata_code import pack_msg,pack_lifedata
from doordata_decode import decode_msg
import socket
import time
from kbic_logging import logger
from multiprocessing import Process , Queue

def sleeptime(hour, min, sec):
    return hour * 3600 + min * 60 + sec

def main(queue):
  DES_IP = "172.18.0.20"
  SOURCE_IP = '172.18.0.22'
  SOURCE_PORT = 8888
  DES_PORT = 8888
# UDP
  client=socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
  client.bind((SOURCE_IP, SOURCE_PORT))
  logger.info('主板数据发送已连接')
  
  while 1 == 1:
    try:
      while not queue.empty():
        data = queue.get() 
        warn_data = decode_msg(data)
        warn_data = pack_msg(warn_data)
        print(warn_data)
        life_data = pack_lifedata()
        second = sleeptime(0, 0, 0.2)
        time.sleep(second)
        try:
          client.sendto(warn_data,(DES_IP, DES_PORT))
          client.sendto(life_data,(DES_IP, DES_PORT))
        except Exception as e:
          logger.error(e)
    except Exception as e:
      logger.error(e)


