import os
import json
from kbic_logging import logger


# software root path
ROOT_PATH = os.getcwd()
# project info
CONFIG_INFO = os.path.join(ROOT_PATH, 'config.json')

signal_config_dict = {'DoorWarn': {}, 'DoorSignal': {}, 'WTDSignal': {}}


def get_signalConfig():
    config = json.load(open(CONFIG_INFO, 'r'), parse_float=True)

    signal_config_dict['DoorWarn'] = config.get('DoorWarn', {})
    signal_config_dict['DoorSignal'] = config.get('DoorSignal', {})
    signal_config_dict['WTDSignal'] = config.get('WTDSignal', {})
    return signal_config_dict


def decode_object(signals_buf, sc):
    obyte = int(sc['byteoffset'])
    obit = int(sc['bitoffset'])
    size = int(sc['len'])
    name = sc.get('name')
    _type = sc.get('type')
    if _type == 'BOOL':  # bit signals
        mask = 0x00
        for i in range(0, size):
            mask = ((0x01 << (obit + i)) | mask)
        tmp = int.from_bytes(signals_buf[obyte:obyte + 1],
                             byteorder='big')
        value = int((tmp & mask) >> obit)
    elif _type.startswith('UNSIGNED'):
        value = int.from_bytes(signals_buf[obyte:obyte + size],
                               byteorder='big')
    else:  # byte, word, double word
        value = signals_buf[obyte:obyte + size]
    # print(sc['name'],value)
    return name, value


def decode_signal(signals_buf, cfg):
    signal = {}
    try:
        for sc in cfg:
            key, value = decode_object(signals_buf, sc)
            signal[key] = value
    except Exception as e:
        logger.error(e)
        signal = {}
    return signal


def decode_msgDCU(signals_buf):
    signal = {}
    try:
        sig_buff = signals_buf
        signal= decode_signal(
            sig_buff, signal_config_dict['DoorWarn'])
    except Exception as e:
        logger.error(e)
        signal = {}
    return signal


def decode_msg(msg):
    msg_dict = {}
    sig_buff = msg
    signal_config_dict = get_signalConfig()
    try:
        msg_dict = decode_signal(sig_buff, signal_config_dict['WTDSignal'])
        for i in range(1,5):
        # for j in range(1,5):
            sig_buff = msg_dict.get('MDCU{}'.format(i), {})
            msg_dict['MDCU{}'.format(i)] = decode_signal(sig_buff,signal_config_dict['DoorSignal'])
            for j in range(1,5):
                warn_data = msg_dict['MDCU{}'.format(i)]['D{}'.format(j)]
                msg_dict['MDCU{}'.format(i)]['D{}'.format(j)] = decode_msgDCU(warn_data)
    except Exception as e:
        logger.error(e)
        msg_dict = {}
    return msg_dict

